import React from 'react';
import styles from './movieList.module.css'; // CSS để tạo kiểu

export default function MovieList({ selectedDay }) {
    const moviesByDays = {
        0: [
            { title: "Captain America: The Winter Soldier", poster: "./movieImage/captain", showtimes: ["10:00", "13:00", "19:00"] },
            { title: "Avengers: Infinity War", poster: "./movieImage/infinityWar", showtimes: ["11:00", "16:00", "21:00"] }
        ],
        1: [
            { title: "Iron Man", poster: "./movieImage/ironMan", showtimes: ["12:00", "15:00", "20:00"] },
            { title: "Spider-Man: No Way Home", poster: "./movieImage/spiderman", showtimes: ["14:00", "18:00"] }
        ],
        2: [
            { title: "Black Panther", poster: "./movieImage/blackPanther", showtimes: ["09:00", "11:00", "17:00"] }
        ],
        default: []
    };

    const calculateDayDiff = (selectedDate) => {
        const today = new Date();
        const selected = new Date(today.getFullYear(), selectedDate.monthIndex, selectedDate.Day);
        const diffTime = selected - today;
        return Math.floor(diffTime / (1000 * 60 * 60 * 24));
    };

    const dayDiff = calculateDayDiff(selectedDay);
    const movies = moviesByDays[dayDiff] || moviesByDays.default;

    return (
        <div className={styles.movieList}>
            <h3>Danh sách phim cho {selectedDay.Month} {selectedDay.Day}:</h3>
            {movies.length > 0 ? (
                <div className={styles.movieGrid}>
                    {movies.map((movie, index) => (
                        <div key={index} className={styles.movieItem}>
                            <img src={movie.poster} alt={movie.title} className={styles.poster} />
                            <h4>{movie.title}</h4>
                            <p>Xuất chiếu: {movie.showtimes.join(', ')}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Không có phim nào.</p>
            )}
        </div>
    );
}
