import React from 'react';

export default function MovieList({ selectedDay }) {
    const moviesByDays = {
        0: ["Movie 1", "Movie 2"],  // Hôm nay
        1: ["Movie 3", "Movie 4"],  // Ngày mai
        2: ["Movie 5"],             // 2 ngày sau
        default: []                 // Những ngày khác
    };

    // Tính số ngày cách biệt giữa ngày được chọn và hôm nay
    const calculateDayDiff = (selectedDate) => {
        const today = new Date();
        const diffTime = new Date(
            today.getFullYear(), today.getMonth(), today.getDate()
        ) - new Date(
            selectedDate.year, selectedDate.monthIndex, selectedDate.Day
        );
        return Math.floor(diffTime / (1000 * 60 * 60 * 24));
    };

    const dayDiff = calculateDayDiff(selectedDay);

    // Lấy danh sách phim theo số ngày cách biệt, nếu không tìm thấy thì dùng list mặc định
    const movies = moviesByDays[dayDiff] || moviesByDays.default;

    return (
        <div>
            <h3>Danh sách phim cho {selectedDay.Month} {selectedDay.Day}:</h3>
            {movies.length > 0 ? (
                <ul>
                    {movies.map((movie, index) => (
                        <li key={index}>{movie}</li>
                    ))}
                </ul>
            ) : (
                <p>Không có phim nào.</p>
            )}
        </div>
    );
}
