import React, { useRef } from "react";
import Slider from "react-slick";
import "../style.css";
import AHDtheaterImg1 from '../image/AHDtheater-1.png';
import AHDtheaterImg2 from '../image/AHDtheater-2.png';
import AHDtheaterImg3 from '../image/AHDtheater-3.png';
import AHDtheaterImg4 from '../image/AHDtheater-4.png';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';

// Nút tùy chỉnh với Font Awesome cho next arrow
function NextArrow(props) {
  const { className, style, onClick } = props;
  return (
  
      <FontAwesomeIcon  
        icon={faArrowRight} 
        className={`${className} custom-arrow next-arrow`} 
        style={{ ...style,  marginRight: "2px"}} 
        onClick={onClick}
      />

  );
}
// Nút tùy chỉnh với Font Awesome cho prev arrow
function PrevArrow(props) {
  const { className, style, onClick } = props;
  return (

      <FontAwesomeIcon  
        icon={faArrowLeft} 
        className={`${className} custom-arrow prev-arrow`} 
        style={{ ...style, marginLeft: "20px" }} 
        onClick={onClick}
        
      />

  );
}


export default function TheaterDetail() {
  const sliderRef = useRef(null); // Sử dụng useRef để tham chiếu đến slider

  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    autoplay: true,           // Bật tính năng tự động chuyển
    autoplaySpeed: 2000,      // Tự chuyển sau mỗi 3000 milliseconds (3 giây)
  };

  return (
    <div>
      <Slider {...settings} ref={sliderRef}>
        <div>
          <img 
              src={AHDtheaterImg1}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg2}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg3}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg4}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
      </Slider>

    </div>
  );
}
