import React, { useState } from 'react';
import './d1.css'; // Nhập file CSS để tạo kiểu
import Next30Days from './GetNext30Days';
export default function DateScroller() {
    // Danh sách các ngày
    const [currentIndex, setCurrentIndex] = useState(0);
    const days = [
        { Day: 17, Dweek: 'Thu', Month: 'Oct' },
        { Day: 18, Dweek: 'Fri', Month: 'Oct' },
        { Day: 19, Dweek: 'Sat', Month: 'Oct' },
        { Day: 20, Dweek: 'Sun', Month: 'Oct' },
        { Day: 21, Dweek: 'Mon', Month: 'Oct' },
        { Day: 22, Dweek: 'Tue', Month: 'Oct' },
        { Day: 23, Dweek: 'Wed', Month: 'Oct' },
        { Day: 24, Dweek: 'Thu', Month: 'Oct' },
        { Day: 25, Dweek: 'Fri', Month: 'Oct' },
        { Day: 26, Dweek: 'Sat', Month: 'Oct' },
        { Day: 27, Dweek: 'Sun', Month: 'Oct' },
        { Day: 28, Dweek: 'Mon', Month: 'Oct' },
        { Day: 29, Dweek: 'Tue', Month: 'Oct' },
        { Day: 30, Dweek: 'Wed', Month: 'Oct' },
        { Day: 31, Dweek: 'Thu', Month: 'Oct' },
        { Day: 1, Dweek: 'Fri', Month: 'Nov' },
        { Day: 2, Dweek: 'Sat', Month: 'Nov' },
        { Day: 3, Dweek: 'Sun', Month: 'Nov' },
        { Day: 4, Dweek: 'Mon', Month: 'Nov' },
        { Day: 5, Dweek: 'Tue', Month: 'Nov' },
        { Day: 6, Dweek: 'Wed', Month: 'Nov' },
        { Day: 7, Dweek: 'Thu', Month: 'Nov' },
        { Day: 8, Dweek: 'Fri', Month: 'Nov' },
        { Day: 9, Dweek: 'Sat', Month: 'Nov' },
        { Day: 10, Dweek: 'Sun', Month: 'Nov' },
    ];

    const handlePrev = () => {
        setCurrentIndex((prevIndex) => Math.max(prevIndex - 1, 0));
    };

    const handleNext = () => {
        setCurrentIndex((prevIndex) => Math.min(prevIndex + 1, days.length - 1));
    };

    return (
        <div className="date-scroller">
            <button onClick={handlePrev} disabled={currentIndex === 0}>&lt;</button>
            <div className="days">
                {days.slice(currentIndex, currentIndex + 5).map((day, index) => (
                    <div key={index} className={`day ${index === 1 ? 'active' : ''}`}>
                        {day}
                    </div>
                ))}
            </div>
            <button onClick={handleNext} disabled={currentIndex === days.length - 5}>&gt;</button>
        </div>
    );
};
