import React from 'react';
import styles from "./cinemaList.module.css"
const Next30Days = () => {
    // Hàm để lấy 30 ngày tiếp theo
    const getNext30Days = () => {
        const days = [];
        const today = new Date();
        const weekdays = ["Su", "Mo", "Tu", "We", "Th", "Fr", "Sa"];
        const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", 
                        "Aug", "Sep", "Oct", "Nov", "Dec"];


        for (let i = 0; i <= 30; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);

            const Day = date.getDate(); // Ngày trong tháng (1-31)
            const Dweek = weekdays[date.getDay()]; // Lấy tên thứ
            const Month = months[date.getMonth()];
            days.push({ Day,Dweek,Month });
        }

        return days;
    };

    const next30Days = getNext30Days();

    return (
        <div>
            <h1>30 ngày tiếp theo:</h1>
            <ul style={{listStyleType: 'none'}}>
                {next30Days.map((day, index) => (
                    <li className={styles.dayBlock} key={index}>
                        <div className={styles.monthAndDweek}>
                            {day.Month}<br/>
                            {day.Dweek}
                        </div>
                        <div className={styles.Day} >
                            {day.Day}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Next30Days;
