import React, { useState } from 'react';
import styles from "./cinemaList.module.css"; // Nhập file CSS để tạo kiểu
import Next30Days from './GetNext30Days';

export default function DateScroller() {
    // Danh sách các ngày
    const [currentIndex, setCurrentIndex] = useState(0);

    // Hàm để lấy 30 ngày tiếp theo
    const getNext30Days = () => {
        const days = [];
        const today = new Date();
        const weekdays = ["Su", "Mo", "Tu", "We", "Th", "Fr", "Sa"];
        const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", 
                        "Aug", "Sep", "Oct", "Nov", "Dec"];

        for (let i = 0; i <= 30; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);
            const Day = date.getDate(); // Ngày trong tháng (1-31)
            const Dweek = weekdays[date.getDay()]; // Lấy tên thứ
            const Month = months[date.getMonth()]; 
            days.push({ Day, Dweek, Month });
        }

        return days;
    };

    const next30Days = getNext30Days();

    const handlePrev = () => {
        setCurrentIndex((prevIndex) => Math.max(prevIndex - 1, 0));
    };

    const handleNext = () => {
        setCurrentIndex((prevIndex) => Math.min(prevIndex + 1, next30Days.length - 1));
    };

    return (
        <div className="date-scroller">
            <button onClick={handlePrev} disabled={currentIndex === 0}>&lt;</button>
            <div className="days">
                {next30Days.slice(currentIndex, currentIndex + 5).map((day, index) => (
                    <div key={index} className={styles.dayBlock}>
                        <div className={styles.monthAndDweek}>
                            {day.Month}<br/>
                            {day.Dweek}
                        </div>
                        <div className={styles.Day} >
                            {day.Day}
                        </div>
                    </div>
                ))}
            </div>

            <button onClick={handleNext} disabled={currentIndex === next30Days.length - 5}>&gt;</button>
        </div>
    );
};
