import React, { useState, useEffect, useRef } from 'react';
import cinemaListStyle from "./cinemaList.module.css";
import TheaterDetail from "./TheaterDetail"
import ProductCollateral from "./ProductCollateral"

export default function CinemaList() {

    // Sử dụng useState để quản lý trạng thái hiển thị của từng tỉnh thành
    const [selectedProvince, setSelectedProvince] = useState(null);
    // Sử dụng useState để quản lý trạng thái hiển thị của rạp phim
    const [selectedTheater, setSelectedTheater] = useState(null);
    // Hàm để xử lý sự kiện khi nhấn vào tỉnh thành


    const handleProvinceClick = (province) => {
        // Nếu tỉnh thành đã chọn là Hồ Chí Minh,..., sẽ ẩn khi nhấn lại, nếu không thì hiển thị
        setSelectedProvince(selectedProvince === province ? null : province);
        setSelectedTheater(null);
        
    };

    // Hàm xử lý khi nhấn vào rạp phim
    const handleTheaterClick = (theater) => {
        if (selectedTheater === theater) {
            // Nếu đã chọn rồi, hủy chọn
            setSelectedTheater(null);
            
        } else {
            // Chọn rạp mới
            setSelectedTheater(theater);
        }
    };

    return (
        <div className={style.theaterWrap}>
            <div className={style.theaterListPreview}>
                <div className= {style.theaterShowtimeTop}></div>
                <div className = {style.theaterShowtimeCenter}>
                    <div className = {style.titleAhdCinema}>
                        <h2>AHD Cinema</h2>
                    </div>
                    <div className = {style.cinemasArea1}>
                        <ul className={style.cinemasByProvince}>
                            <li onClick={() => handleProvinceClick("TPHoChiMinh")}>
                                Hồ Chí Minh
                                {selectedProvince === "TPHoChiMinh" && (
                                    <span className={style.underline}></span>
                                )}
                            </li>
                            <li onClick={() => handleProvinceClick("HaNoi")}>
                                Hà Nội
                                {selectedProvince === "HaNoi" && (
                                    <span className={style.underline}></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("DaNang")}>
                                Đà Nẵng
                                {selectedProvince === "DaNang" && (
                                    <span className={style.underline}></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("HaiPhong")}>
                                Hải Phòng
                                {selectedProvince === "HaiPhong" && (
                                    <span className={style.underline}></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("CanTho")}>
                                Cần Thơ
                                {selectedProvince === "CanTho" && (
                                    <span className={style.underline}></span>
                                )}
                            </li> 
                        </ul>
                    </div>
                    <div className ={style.cinemasArea2}>
                        <ul className ={style.cinemasList}>
                        {selectedProvince === "TPHoChiMinh" && (
                            <>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Hùng Vương Plaza")}>AHD Hùng Vương Plaza</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Crescent Mall")}>AHD Crescent Mall</li>
                            <li className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Thảo Điền Pearl")}>AHD Thảo Điền Pearl</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Vincom Thủ Đức")}>AHD Vincom Thủ Đức</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Vivo City")}>AHD Vivo City</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Pearl Plaza")}>AHD Pearl Plaza</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Liberty Citypoint")}>AHD Liberty Citypoint</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Vincom Đồng Khởi")}>AHD Vincom Đồng Khởi</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Menas Mall")}>AHD Menas Mall</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Pandora City")}>AHD Pandora City</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Aeon Tân Phú")}>AHD Aeon Tân Phú</li>
                            <li  className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Vincom Gò Vấp")}>AHD Vincom Gò Vấp</li>
                            <li className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Hoàng Văn Thụ")}>AHD Hoàng Văn Thụ</li>
                            <li className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Aeon Bình Tân")}>AHD Aeon Bình Tân</li>
                            <li className={style.ahdCity1} onClick={() => handleTheaterClick("AHD Saigonres Nguyễn Xí")}>AHD Saigonres Nguyễn Xí</li>
                            </>
                        )}
                        {selectedProvince === "HaNoi" && (
                            <>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Vincom Bà Triệu")}>AHD Vincom Bà Triệu</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Hồ Gươm Plaza")}>AHD Hồ Gươm Plaza</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Aeon Long Biên")}>AHD Aeon Long Biên</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Vincom Nguyễn Chí Thanh")}>AHD Vincom Nguyễn Chí Thanh</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Indochine Plaza Hà Nội")}>AHD Indochine Plaza Hà Nội</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Rice City")}>AHD Rice City</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Hà Nội Centerpoint")}>AHD Hà Nội Centerpoint</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Vincom Royal City")}>AHD Vincom Royal City</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Vincom Times City")}>AHD Vincom Times City</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Vincom Long Biên")}>AHD Vincom Long Biên</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Mac Plaza")}>AHD Mac Plaza</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Trương Định Plaza")}>AHD Trương Định Plaza</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Tràng Tiền Plaza")}>AHD Tràng Tiền Plaza</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Sun Grand Thụy Khuê")}>AHD Sun Grand Thụy Khuê</li>
                            <li className={style.ahdCity2} onClick={() => handleTheaterClick("AHD Sun Grand Lương Yên")}>AHD Sun Grand Lương Yên</li>
                            </>
                        )}
                        {selectedProvince === "DaNang" && (
                            <>
                            <li  className={style.ahdCity3} onClick={() => handleTheaterClick("AHD Vĩnh Trung Plaza")}>AHD Vĩnh Trung Plaza</li>
                            <li  className={style.ahdCity3} onClick={() => handleTheaterClick("AHD Vincom Đà Nẵng")}>AHD Vincom Đà Nẵng</li>
                            </>
                        )}
                        {selectedProvince === "HaiPhong" && (
                            <>
                            <li  className={style.ahdCity4} onClick={() => handleTheaterClick("AHD Vincom Hải Phòng")}>AHD Vincom Hải Phòng</li>
                            <li  className={style.ahdCity4} onClick={() => handleTheaterClick("AHD Aeon Mall Hải Phòng")}>AHD Aeon Mall Hải Phòng</li>
                            </>
                        )}
                        {selectedProvince === "CanTho" && (
                            <>
                            <li  className={style.ahdCity5} onClick={() => handleTheaterClick("AHD Sense City")}>AHD Sense City</li>
                            <li  className={style.ahdCity5} onClick={() => handleTheaterClick("AHD Vincom Xuân Khánh")}>AHD Vincom Xuân Khánh</li>
                            <li  className={style.ahdCity5} onClick={() => handleTheaterClick("AHD Vincom Hùng Vương")}>AHD Vincom Hùng Vương</li>
                            </>
                        )}
                        </ul> 
                    </div>
                </div>
                <div className = {style.theaterShowtimeBottom}></div>
            </div>
            <div className ={style.theaterContainerProductPreview} >
                {
                    selectedProvince 
                  && selectedTheater
                  &&(<>
                        <div className={style.theaterLine}>
                            <div className={style.line}></div>
                            <h2 className={style.theaterTitle} >THEATER</h2>
                            <div className={style.line}></div>
                        </div>
                        <h3 className={style.selectedTheater} >{selectedTheater}</h3>
                        {/* <TheaterDetail/>
                        <ProductCollateral/> */}
                   </>)
                }     
            </div>
        </div>
    );
}







  



