import "../style.css";
import React from 'react';
export default function MemberInfo() {
    document.addEventListener('DOMContentLoaded', () => {
        const citySelect = document.getElementById('city');
    
        const districtDatalist = document.getElementById('districts');
    
        const districtsByCity = {
            "Ho Chi Minh": [
                "Quận 1",
                "Quận 2",
                "Quận 3",
                "Quận 4",
                "Quận 5",
                "Quận 6",
                "Quận 7",
                "Quận 8",
                "Quận 9",
                "Quận 10",
                "Quận 11",
                "Quận 12",
                "Bình Tân",
                "Bình Thạnh",
                "Gò Vấp",
                "Phú Nhuận",
                "Tân Bình",
                "Tân Phú",
                "Thủ Đức"
            ],
            "Ha Noi": [
                "Ba Đình",
                "Hoàn Kiếm",
                "Tây Hồ",
                "Long Biên",
                "Cầu Giấy",
                "Đống Đa",
                "Hai Bà Trưng",
                "Hoàng Mai",
                "Thanh Xuân",
                "Sóc Sơn",
                "Đông Anh",
                "Gia Lâm",
                "Nam Từ Liêm",
                "Bắc Từ Liêm",
                "Hà Đông"
            ],
            "Da Nang": [
                "Hải Châu",
                "Sơn Trà",
                "Ngũ Hành Sơn",
                "Liên Chiểu",
                "Cẩm Lệ",
                "Hoàng Sa",
                "Hòa Vang",
                "Thanh Khê",
                "Cẩm Sơn"
            ]
            // Thêm các thành phố và quận/huyện khác nếu cần
        };
    
        // Hàm cập nhật danh sách quận/huyện
        function updateDistricts() {
            const selectedCity = citySelect.value;
            // Xóa các tùy chọn hiện tại
            districtDatalist.innerHTML = '';
    
            if (districtsByCity[selectedCity]) {
                districtsByCity[selectedCity].forEach(district => {
                    const option = document.createElement('option');
                    option.value = district;
                    districtDatalist.appendChild(option);
                });
            }
        }
    
        // Lắng nghe sự kiện thay đổi của trường "Thành phố"
        citySelect.addEventListener('change', updateDistricts);
    
        // Biên thực hiện kiểm tra khi submit form
        const form = document.getElementById('memberForm');
    
        form.addEventListener('submit', (e) => {
            let valid = true;
    
            // Các trường bắt buộc
            const requiredFields = ['fullname', 'email', 'phone', 'dob', 'cmnd'];
    
            for (let field of requiredFields) {
                const input = document.getElementById(field);
                if (!input.value.trim()) {
                    const fieldLabel = input.previousElementSibling.textContent.replace(':', '').replace('*', '').trim();
                    alert('Vui lòng nhập ' + fieldLabel + '!'); // Thông báo cho người dùng
                    input.focus(); // Đưa con trỏ đến ô chưa được điền
                    valid = false; // Đánh dấu rằng biểu mẫu không hợp lệ
                    e.preventDefault(); // Ngăn chặn việc gửi biểu mẫu
                    break; // Thoát khỏi vòng lặp
                }
            }
    
            if (valid) {
                alert('Cập nhật thông tin thành công!');
            }
        });
    });
    
    // Hàm xem trước hình ảnh
    function previewImage(event) {
        const imagePreview = document.getElementById('imagePreview');
        const file = event.target.files[0];
    
        if (file) {
            const reader = new FileReader();
            reader.onload = function(e) {
                imagePreview.style.backgroundImage = `url(${e.target.result})`;
                imagePreview.style.backgroundSize = 'cover';
                imagePreview.style.backgroundPosition = 'center';
                imagePreview.innerHTML = ''; // Xóa chữ "No Image"
            }
            reader.readAsDataURL(file);
        }
    }
    return (
      <div>
           <h2>Thông tin tài khoản</h2>

<form id="memberForm" action="/update" method="post" enctype="multipart/form-data" novalidate>
   
    <div className="image-upload-container">
        <div id="imagePreview" className="image-preview" onclick="document.getElementById('profilePicture').click();">
            <span>No Image</span>
        </div>
        <input type="file" id="profilePicture" name="profilePicture" accept="image/*" style={{display: 'none'}} onChange={previewImage(event)}/>

        <button type="button" id="uploadButton" onClick={document.getElementById('profilePicture').click()}>Tải ảnh lên</button> 
    </div>

    
    <div className="form-row">
        <div className="form-group">
            <label for="fullname"><span className="highlight">* </span>Họ tên:</label>
            <input type="text" id="fullname" name="fullname" className="input" placeholder="Họ tên" required/>
        </div>

        <div className="form-group">
            <label for="email"><span className="highlight">* </span>Email:</label>
            <input type="email" id="email" name="email" className="input" placeholder="Email" required/>
        </div>
    </div>

    <div className="form-row">
        
        <div className="form-group">
            <img src="/public/image/phone.png" alt="Icon" className="input-icon"/>
            <label htmlFor="phone"><span className="highlight">* </span>Số điện thoại:</label>
            <input type="tel" id="phone" name="phone" className="input" placeholder="Số điện thoại" required/>
        </div>

        <div className="form-group">
            <label htmlFor="cmnd"><span className="highlight">* </span>CMND/Hộ chiếu:</label>
            <input type="text" id="cmnd" name="cmnd" className="input" placeholder="CMND/Hộ chiếu" required/>
        </div>
    </div>

    <div className="form-row">
        
        <div className="form-group">
