import React, { useRef } from "react";
import Slider from "react-slick";
import "../style.css";
import AHDtheaterImg1 from '../image/AHDtheater-1.png';
import AHDtheaterImg2 from '../image/AHDtheater-2.png';
import AHDtheaterImg3 from '../image/AHDtheater-3.png';
import AHDtheaterImg4 from '../image/AHDtheater-4.png';

export default function TheaterDetail() {
  const sliderRef = useRef(null); // Sử dụng useRef để tham chiếu đến slider

  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div>
      <Slider {...settings} >
        <div>
          <img 
              src={AHDtheaterImg1}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg2}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg3}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg4}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
      </Slider>

    </div>
  );
}
