import React, { useState, useEffect } from 'react';
import "../style.css";

export default function CinemaList() {

    // Sử dụng useState để quản lý trạng thái hiển thị của từng tỉnh thành
    const [selectedProvince, setSelectedProvince] = useState(null);
    // Sử dụng useState để quản lý trạng thái hiển thị của rạp phim
    const [selectedTheater, setSelectedTheater] = useState(null);
    // Hàm để xử lý sự kiện khi nhấn vào tỉnh thành

    const handleProvinceClick = (province) => {
        // Nếu tỉnh thành đã chọn là Hồ Chí Minh,..., sẽ ẩn khi nhấn lại, nếu không thì hiển thị
        setSelectedProvince(selectedProvince === province ? null : province);
    };

    const handleTheaterClick = (theater) => {
        setSelectedTheater(selectedTheater === theater ? null : theater);
    };

    return (
        <div className="theater-wrap">
            <div className="theater-list-preview">
                <div className= "theater-showtime-top"></div>
                <div className = "theater-showtime-center">
                    <div className = "title-ahdCinema">
                        <h2>AHD Cinema</h2>
                    </div>
                    <div className = "cinemas-area-1">
                        <ul className="cinemas-byProvince">
                            <li onClick={() => handleProvinceClick("TPHoChiMinh")}>Hồ Chí Minh</li>
                            <li onClick={() => handleProvinceClick("HaNoi")}>Hà Nội</li> 
                            <li onClick={() => handleProvinceClick("DaNang")}>Đà Nẵng</li> 
                            <li onClick={() => handleProvinceClick("HaiPhong")}>Hải Phòng</li> 
                            <li onClick={() => handleProvinceClick("CanTho")}>Cần Thơ</li> 
                        </ul>
                    <div className ="cinemas-area-2">
                        <ul className ="cinemas-list">
                        {selectedProvince === "TPHoChiMinh" && (
                            <>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Hùng Vương Plaza")}>AHD Hùng Vương Plaza</li>
                            <li  className="ahd-city-1">AHD Crescent Mall</li>
                            <li className="ahd-city-1">AHD Thảo Điền Pearl</li>
                            <li  className="ahd-city-1">AHD Vincom Thủ Đức</li>
                            <li  className="ahd-city-1">AHD Vivo City</li>
                            <li  className="ahd-city-1">AHD Pearl Plaza</li>
                            <li  className="ahd-city-1">AHD Liberty Citypoint</li>
                            <li  className="ahd-city-1">AHD Vincom Đồng Khởi</li>
                            <li  className="ahd-city-1">AHD Menas Mall</li>
                            <li  className="ahd-city-1">AHD Pandora City</li>
                            <li  className="ahd-city-1">AHD Aeon Tân Phú</li>
                            <li  className="ahd-city-1">AHD Vincom Gò Vấp</li>
                            <li className="ahd-city-1">AHD Hoàng Văn Thụ</li>
                            <li className="ahd-city-1">AHD Aeon Bình Tân</li>
                            <li className="ahd-city-1">AHD Saigonres Nguyễn Xí</li>
                            </>
                        )}
                        {selectedProvince === "HaNoi" && (
                            <>
                            <li className="ahd-city-2">AHD Vincom Bà Triệu</li>
                            <li className="ahd-city-2">AHD Hồ Gươm Plaza</li>
                            <li className="ahd-city-2">AHD Aeon Long Biên</li>
                            <li className="ahd-city-2">AHD Vincom Nguyễn Chí Thanh</li>
                            <li className="ahd-city-2">AHD Indochine Plaza Hà Nội</li>
                            <li className="ahd-city-2">AHD Rice City</li>
                            <li className="ahd-city-2">AHD Hà Nội Centerpoint</li>
                            <li className="ahd-city-2">AHD Vincom Royal City</li>
                            <li className="ahd-city-2">AHD Vincom Times City</li>
                            <li className="ahd-city-2">AHD Vincom Long Biên</li>
                            <li className="ahd-city-2">AHD Mac Plaza</li>
                            <li className="ahd-city-2">AHD Trương Định Plaza</li>
                            <li className="ahd-city-2">AHD Tràng Tiền Plaza</li>
                            <li className="ahd-city-2">AHD Sun Grand Thụy Khuê</li>
                            <li className="ahd-city-2">AHD Sun Grand Lương Yên</li>
                            </>
                        )}
                        
                        </ul> 
                    </div>
                </div>
                </div>
                <div className = "theater-showtime-bottom"></div>
            </div>
            <div className ="theater-container-product-preview">
                  {/* <h3 className="selected-theater">{selectedTheater}</h3> */}
                  {selectedTheater && (
                    <h3 className="selected-theater">Bạn đã chọn: {selectedTheater}</h3>
                )}     
            </div>
        </div>
    );
}

