    "AHD Vincom Đồng Khởi", "AHD Menas Mall","AHD Pandora City", "AHD Aeon Tân Phú", "AHD Vincom Gò Vấp",
    "AHD Hoàng Văn Thụ","AHD Aeon Bình Tân", "AHD Saigonres Nguyễn Xí"
  ],
  HaNoi: [
    "AHD Vincom Bà Triệu", "AHD Hồ Gươm Plaza"
  ],
  // Thêm rạp cho các tỉnh khác...
};

export default function CinemaList() {
  const [selectedProvince, setSelectedProvince] = useState(null);
  const [selectedTheater, setSelectedTheater] = useState(null);

  const handleProvinceClick = (provinceKey) => {
    setSelectedProvince(provinceKey === selectedProvince ? null : provinceKey);
    setSelectedTheater(null);
  };

  const handleTheaterClick = (theater) => {
    setSelectedTheater(selectedTheater === theater ? null : theater);
  };

  return (
    <div className={style.theaterWrap}>
      <div className={style.theaterListPreview}>
        <h2 className={style.titleAhdCinema}>AHD Cinema</h2>

        <ul className={style.cinemasByProvince}>
          {provinces.map((province) => (
            <li key={province.key} onClick={() => handleProvinceClick(province.key)}>
              {province.name}
              {selectedProvince === province.key && <span className={style.underline}></span>}
            </li>
          ))}
        </ul>

        <ul className={style.cinemasList}>
          {theatersByProvince[selectedProvince]?.map((theater) => (
            <li key={theater} onClick={() => handleTheaterClick(theater)}>
              {theater}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
