import React, { useRef } from "react";
import Slider from "react-slick";
import "../style.css";
import AHDtheaterImg1 from '../image/AHDtheater-1.png';
import AHDtheaterImg2 from '../image/AHDtheater-2.png';
import AHDtheaterImg3 from '../image/AHDtheater-3.png';
import AHDtheaterImg4 from '../image/AHDtheater-4.png';

import { faArrowUp, faArrowDown, faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';

// Nút tùy chỉnh với Font Awesome cho next arrow
function NextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} custom-arrow next-arrow`}
      style={{ ...style }}
      onClick={onClick}
    >
      <FontAwesomeIcon  icon={faArrowRight} >::before</FontAwesomeIcon>
    </div>
  );
}

// Nút tùy chỉnh với Font Awesome cho prev arrow
function PrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} custom-arrow prev-arrow`}
      style={{ ...style }}
      onClick={onClick}
    >
      <FontAwesomeIcon  icon={faArrowLeft} >::before</FontAwesomeIcon>
    </div>
  );
}


export default function TheaterDetail() {
  const sliderRef = useRef(null); // Sử dụng useRef để tham chiếu đến slider

  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
  };

  return (
    <div>
      <Slider {...settings} ref={sliderRef}>
        <div>
          <img 
              src={AHDtheaterImg1}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg2}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg3}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
        <div>
          <img 
              src={AHDtheaterImg4}
              alt="AHD Theater"
              className="slider-img"
          />
        </div>
      </Slider>

    </div>
  );
}
