import React from 'react';
import styles from "./cinemaList.module.css"
const Next30Days = () => {
    // Hàm để lấy 30 ngày tiếp theo
    const getNext30Days = () => {
        const days = [];
        const today = new Date();

        for (let i = 0; i <= 30; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);
            days.push(date.toLocaleDateString('en-EN', { weekday: 'long', month: 'long', day: 'numeric' }));
        }

        return days;
    };

    const next30Days = getNext30Days();

    return (
        <div>
            <h1>30 ngày tiếp theo:</h1>
            <ul style={{listStyleType: 'none'}}>
                {next30Days.map((day, index) => (
                    <li className={styles.dayBlock} key={index}>{day}
                    
                    {day.month}</li>
                ))}
            </ul>
        </div>
    );
};

export default Next30Days;
