import React, { useState } from 'react';
import './d1.css'; // Nhập file CSS để tạo kiểu

export default function DateScroller() {
    // Danh sách các ngày
    const [currentIndex, setCurrentIndex] = useState(0);
    const days = [
        'Thu 17',
        'Fri 18',
        'Sat 19',
        'Sun 20',
        'Mon 21',
        'Tue 22',
        'Wed 23',
        'Thu 24',
        'Fri 25',
        'Sat 26',
        'Sun 27'
    ];

    const handlePrev = () => {
        setCurrentIndex((prevIndex) => Math.max(prevIndex - 1, 0));
    };

    const handleNext = () => {
        setCurrentIndex((prevIndex) => Math.min(prevIndex + 1, days.length - 1));
    };

    return (
        <div className="date-scroller">
            <button onClick={handlePrev} disabled={currentIndex === 0}>&lt;</button>
            <div className="days">
                {days.slice(currentIndex, currentIndex + 8).map((day, index) => (
                    <div key={index} className={`day ${index === 1 ? 'active' : ''}`}>
                        {day}
                    </div>
                ))}
            </div>
            <button onClick={handleNext} disabled={currentIndex === days.length - 8}>&gt;</button>
        </div>
    );
};
