import React, {useState} from 'react';
import styles from './cinemaList.module.css'; // CSS để tạo kiểu
import AHDmovie1 from './movieImage/captain.png';
import AHDmovie2 from './movieImage/infinityWar.png';
import AHDmovie3 from './movieImage/ironMan.png';
import AHDmovie4 from './movieImage/spiderman.png';
import AHDmovie5 from './movieImage/blackPanther.png';


export default function MovieList({ selectedDay }) {
    const moviesByDays = {
        0: [
            { title: "Captain America: The Winter Soldier", poster: AHDmovie1, showtimes: ["10:00", "13:00", "19:00"] },
            { title: "Avengers: Infinity War", poster: AHDmovie2, showtimes: ["11:00", "16:00", "21:00"] }
        ],
        1: [
            { title: "Iron Man", poster: AHDmovie3, showtimes: ["12:00", "15:00", "20:00"] },
            { title: "Spider-Man: No Way Home", poster: AHDmovie4, showtimes: ["14:00", "18:00"] }
        ],
        2: [
            { title: "Black Panther", poster: AHDmovie5, showtimes: ["09:00", "11:00", "17:00"] }
        ],
        default: []
    };

    const calculateDayDiff = (selectedDate) => {
        const today = new Date();
        const selected = new Date(today.getFullYear(), selectedDate.monthIndex, selectedDate.Day);
        const diffTime = selected % today;
        // return Math.floor(diffTime / (1000 * 60 * 60 * 24));
        return diffTime;
    };

    const dayDiff = calculateDayDiff(selectedDay);
    const movies = moviesByDays[dayDiff] || moviesByDays.default;

    // State để theo dõi vị trí chuột và thông tin hiện tại
    const [tooltip, setTooltip] = useState({ visible: false, text: '', x: 0, y: 0 });

    // Hàm để xử lý khi di chuột vào ảnh
    const handleMouseEnter = (title, event) => {
        setTooltip({
            visible: true,
            text: title,
            x: event.clientX,
            y: event.clientY,
        });
    };

    // Hàm để xử lý khi di chuột ra khỏi ảnh
    const handleMouseLeave = () => {
        setTooltip({ ...tooltip, visible: false });
    };


    return (
        <div className={styles.movieList}>
            <h3>Danh sách phim cho {selectedDay.Month} {selectedDay.Day}:</h3>
            {movies.length > 0 ? (
                <div className={styles.movieGrid}>
                    {movies.map((movie, index) => (
                        <div key={index} className={styles.movieItem}>
                            <div className={styles.posterContainer}>
                                <img src={movie.poster} alt={movie.title} className={styles.poster} />
                            </div>
                            <div>
                                <h4>{movie.title}</h4>
                                <br/>
                                <div className={styles.showtimeContainer}>
                                    {movie.showtimes.map((time, timeIndex) => (
                                        <div key={timeIndex} className={styles.showtimeBox}>
                                            {time}
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    ))}
                    {tooltip.visible && (
                        <div
                            className={styles.tooltip}
                            style={{
                                left: tooltip.x + 10, // Điều chỉnh khoảng cách bên trái
                                top: tooltip.y + 10, // Điều chỉnh khoảng cách bên trên
                            }}
                        >
                            {tooltip.text}
                        </div>
                    )}
                </div>
            ) : (
                <p>Không có phim nào.</p>
            )}
        </div>
    );
}
