import React, { useState, useEffect, useRef } from 'react';
import "../style.css";
import AHDtheaterImg1 from '../image/AHDtheater-1.png';
import AHDtheaterImg2 from '../image/AHDtheater-2.png';
import AHDtheaterImg3 from '../image/AHDtheater-3.png';


export default function CinemaList() {

    // Sử dụng useState để quản lý trạng thái hiển thị của từng tỉnh thành
    const [selectedProvince, setSelectedProvince] = useState(null);
    // Sử dụng useState để quản lý trạng thái hiển thị của rạp phim
    const [selectedTheater, setSelectedTheater] = useState(null);
    const [selectedImages, setSelectedImages] = useState([]); // Quản lý ảnh rạp
    const [currentImageIndex, setCurrentImageIndex] = useState(0); // Index hình ảnh hiện tại
    const [loaded, setLoaded] = useState(false); // Để áp dụng hiệu ứng fade
    const slideInterval = useRef(null); // Reference để quản lý interval
    // Hàm để xử lý sự kiện khi nhấn vào tỉnh thành


    const handleProvinceClick = (province) => {
        // Nếu tỉnh thành đã chọn là Hồ Chí Minh,..., sẽ ẩn khi nhấn lại, nếu không thì hiển thị
        setSelectedProvince(selectedProvince === province ? null : province);
        setSelectedTheater(null);
        setSelectedImages([]);
        setCurrentImageIndex(0);
        setLoaded(false);
        stopAutoSlide(); // Dừng auto-slide khi thay đổi tỉnh thành
    };

    // Hàm xử lý khi nhấn vào rạp phim
    const handleTheaterClick = (theater) => {
        if (selectedTheater === theater) {
            // Nếu đã chọn rồi, hủy chọn
            setSelectedTheater(null);
            setSelectedImages([]);
            setCurrentImageIndex(0);
            setLoaded(false);
            stopAutoSlide(); // Dừng auto-slide khi hủy chọn
        } else {
            // Chọn rạp mới
            setSelectedTheater(theater);
            // Cập nhật mảng ảnh tương ứng với rạp
            switch (theater) {
                case 'AHD Hùng Vương Plaza':
                    setSelectedImages([AHDtheaterImg1, AHDtheaterImg2, AHDtheaterImg3]);
                    break;
                case 'AHD Crescent Mall':
                    setSelectedImages([AHDtheaterImg2]);
                    break;
                case 'AHD Thảo Điền Pearl':
                    setSelectedImages([AHDtheaterImg3]);
                    break;
                // Thêm các case khác nếu cần
                default:
                    setSelectedImages([]);
                    break;
            }
            setCurrentImageIndex(0);
            setLoaded(false);
            startAutoSlide(); // Bắt đầu auto-slide khi chọn rạp mới
        }
    };
     // Hàm chuyển ảnh trước
    const handlePrevClick = () => {
        if (selectedImages.length === 0) return;
        setCurrentImageIndex((prevIndex) =>
            prevIndex === 0 ? selectedImages.length - 1 : prevIndex - 1
        );
        setLoaded(false);
    };

    // Hàm chuyển ảnh tiếp
    const handleNextClick = () => {
        if (selectedImages.length === 0) return;
        setCurrentImageIndex((prevIndex) =>
            prevIndex === selectedImages.length - 1 ? 0 : prevIndex + 1
        );
        setLoaded(false);
    };

    // Hàm bắt đầu auto-slide
    const startAutoSlide = () => {
        stopAutoSlide(); // Đảm bảo chỉ có một interval hoạt động
        if (selectedImages.length > 1) {
            slideInterval.current = setInterval(() => {
                handleNextClick();
            }, 5000); // Chuyển đổi mỗi 5 giây
        }
    };

    // Hàm dừng auto-slide
    const stopAutoSlide = () => {
        if (slideInterval.current) {
            clearInterval(slideInterval.current);
            slideInterval.current = null;
        }
    };

    // Hàm xử lý khi di chuột vào slideshow
    const handleMouseEnter = () => {
        stopAutoSlide();
    };

    // Hàm xử lý khi di chuột ra khỏi slideshow
    const handleMouseLeave = () => {
        startAutoSlide();
    };

    // Auto-slide khi component mount hoặc selectedImages thay đổi
    useEffect(() => {
        if (selectedImages.length > 1) {
            startAutoSlide();
        }
        return () => {
            stopAutoSlide(); // Dọn dẹp khi component unmount
        };
    }, [selectedImages]);
    return (
        <div className="theater-wrap">
            <div className="theater-list-preview">
                <div className= "theater-showtime-top"></div>
                <div className = "theater-showtime-center">
                    <div className = "title-ahdCinema">
                        <h2>AHD Cinema</h2>
                    </div>
                    <div className = "cinemas-area-1">
                        <ul className="cinemas-byProvince">
                            <li onClick={() => handleProvinceClick("TPHoChiMinh")}>
                                Hồ Chí Minh
                                {selectedProvince === "TPHoChiMinh" && (
                                    <span className="underline"></span>
                                )}
                            </li>
                            <li onClick={() => handleProvinceClick("HaNoi")}>
                                Hà Nội
                                {selectedProvince === "HaNoi" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("DaNang")}>
                                Đà Nẵng
                                {selectedProvince === "DaNang" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("HaiPhong")}>
                                Hải Phòng
                                {selectedProvince === "HaiPhong" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("CanTho")}>
                                Cần Thơ
                                {selectedProvince === "CanTho" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                        </ul>
                    </div>
                    <div className ="cinemas-area-2">
                        <ul className ="cinemas-list">
                        {selectedProvince === "TPHoChiMinh" && (
                            <>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Hùng Vương Plaza")}>AHD Hùng Vương Plaza</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Crescent Mall")}>AHD Crescent Mall</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Thảo Điền Pearl")}>AHD Thảo Điền Pearl</li>
                            <li  className="ahd-city-1"onClick={() => handleTheaterClick("AHD Vincom Thủ Đức")}>AHD Vincom Thủ Đức</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vivo City")}>AHD Vivo City</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Pearl Plaza")}>AHD Pearl Plaza</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Liberty Citypoint")}>AHD Liberty Citypoint</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Đồng Khởi")}>AHD Vincom Đồng Khởi</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Menas Mall")}>AHD Menas Mall</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Pandora City")}>AHD Pandora City</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Aeon Tân Phú")}>AHD Aeon Tân Phú</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Gò Vấp")}>AHD Vincom Gò Vấp</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Hoàng Văn Thụ")}>AHD Hoàng Văn Thụ</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Aeon Bình Tân")}>AHD Aeon Bình Tân</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Saigonres Nguyễn Xí")}>AHD Saigonres Nguyễn Xí</li>
                            </>
                        )}
                        {selectedProvince === "HaNoi" && (
                            <>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Bà Triệu")}>AHD Vincom Bà Triệu</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Hồ Gươm Plaza")}>AHD Hồ Gươm Plaza</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Aeon Long Biên")}>AHD Aeon Long Biên</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Nguyễn Chí Thanh")}>AHD Vincom Nguyễn Chí Thanh</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Indochine Plaza Hà Nội")}>AHD Indochine Plaza Hà Nội</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Rice City")}>AHD Rice City</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Hà Nội Centerpoint")}>AHD Hà Nội Centerpoint</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Royal City")}>AHD Vincom Royal City</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Times City")}>AHD Vincom Times City</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Long Biên")}>AHD Vincom Long Biên</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Mac Plaza")}>AHD Mac Plaza</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Trương Định Plaza")}>AHD Trương Định Plaza</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Tràng Tiền Plaza")}>AHD Tràng Tiền Plaza</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Sun Grand Thụy Khuê")}>AHD Sun Grand Thụy Khuê</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Sun Grand Lương Yên")}>AHD Sun Grand Lương Yên</li>
                            </>
                        )}
                        {selectedProvince === "DaNang" && (
                            <>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vĩnh Trung Plaza")}>AHD Vĩnh Trung Plaza</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Đà Nẵng")}>AHD Vincom Đà Nẵng</li>
                            </>
                        )}
                        {selectedProvince === "HaiPhong" && (
                            <>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Hải Phòng")}>AHD Vincom Hải Phòng</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Aeon Mall Hải Phòng")}>AHD Aeon Mall Hải Phòng</li>
                            </>
                        )}
                        {selectedProvince === "CanTho" && (
                            <>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Sense City")}>AHD Sense City</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Xuân Khánh")}>AHD Vincom Xuân Khánh</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Hùng Vương")}>AHD Vincom Hùng Vương</li>
                            </>
                        )}
                        </ul> 
                    </div>
                </div>
                <div className = "theater-showtime-bottom"></div>
            </div>
            <div className ="theater-container-product-preview" >
                {
                    selectedProvince 
                  && selectedTheater
                  &&(<>
                        <div className="theater-line">
                            <div className="line"></div>
                            <h2 className="theater-title" >THEATER</h2>
                            <div className="line"></div>
                        </div>
                        <h3 className="selected-theater" >{selectedTheater}</h3>
           {/* Hiển thị slideshow */}
           {selectedImages.length > 0 && (
                            <div 
                                className="theater-slideshow" 
                                onMouseEnter={handleMouseEnter} 
                                onMouseLeave={handleMouseLeave}
                            >
                                <button className="prev-button" onClick={handlePrevClick}>❮</button>
                                <div className="slideshow-images-wrapper">
                                    <div 
                                        className="slideshow-images-container"
                                        style={{ transform: `translateX(-${currentImageIndex * 100}%)` }}
                                    >
                                        {selectedImages.map((img, index) => (
                                            <img 
                                                key={index} 
                                                src={img} 
                                                alt={`${selectedTheater} ${index + 1}`} 
                                                className={`slideshow-image ${currentImageIndex === index ? 'loaded' : ''}`}
                                                onLoad={() => setLoaded(true)}
                                            />
                                        ))}
                                    </div>
                                </div>
                                <button className="next-button" onClick={handleNextClick}>❯</button>
                            </div>
                        )}
                   </>)
                }     
            </div>
        </div>
    );
}
