import React, { useState, useEffect } from 'react';
import "../style.css";

export default function MemberInfo() {
  const [formData, setFormData] = useState({
    fullname: '',
    email: '',
    phone: '',
    cmnd: '',
    dob: '',
    city: '',
    district: '',
    gender: '', 
    address: '', 
    profilePicture: null
  });
  const [districts, setDistricts] = useState([]);

  const districtsByCity = {
    "Ho Chi Minh": [
            "Quận 1",
            "Quận 2",
            "Quận 3",
            "Quận 4",
            "Quận 5",
            "Quận 6",
            "Quận 7",
            "Quận 8",
            "Quận 9",
            "Quận 10",
            "Quận 11",
            "Quận 12",
            "Bình Tân",
            "Bình Thạnh",
            "Gò Vấp",
            "Phú Nhuận",
            "Tân Bình",
            "Tân Phú",
            "Thủ Đức"
        ],
    "Ha Noi": [
      "Ba Đình",
      "Hoàn Kiếm",
      "Tây Hồ",
      "Long Biên",
      "Cầu Giấy",
      "Đống Đa",
      "Hai Bà Trưng",
      "Hoàng Mai",
      "Thanh Xuân",
      "Sóc Sơn",
      "Đông Anh",
      "Gia Lâm",
      "Nam Từ Liêm",
      "Bắc Từ Liêm",
      "Hà Đông"
  ],
    
    "Da Nang": [
            "Hải Châu",
            "Sơn Trà",
            "Ngũ Hành Sơn",
            "Liên Chiểu",
            "Cẩm Lệ",
            "Hoàng Sa",
            "Hòa Vang",
            "Thanh Khê",
            "Cẩm Sơn"
      ],

      "Hai Phong": [
        "Hồng Bàng",
        "Lê Chân",
        "Ngô Quyền",
        "Kiến An",
        "Hải An",
        "Dương Kinh",
        "Đồ Sơn",
        "An Dương",
        "An Lão",
        "Kiến Thụy",
        "Tiên Lãng",
        "Vĩnh Bảo",
        "Cát Hải",
        "Bạch Long Vĩ",
        "Thủy Nguyên"
    ],
    "Can Tho": [
      "Ninh Kiều",
      "Bình Thủy",
      "Cái Răng",
      "Ô Môn",
      "Thốt Nốt",
      "Phong Điền",
      "Cờ Đỏ",
      "Thới Lai",
      "Vĩnh Thạnh"
  ]

    // Thêm các thành phố khác
  };

  // Cập nhật quận/huyện khi thay đổi thành phố
  useEffect(() => {
    if (formData.city) {
      setDistricts(districtsByCity[formData.city] || []);
    }
  }, [formData.city]);

  // Hàm xử lý khi thay đổi dữ liệu
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Hàm xem trước hình ảnh
  const previewImage = (event) => {
    const file = event.target.files[0];
    setFormData({ ...formData, profilePicture: file });
    
    const reader = new FileReader();
    reader.onload = function(e) {
      const imagePreview = document.getElementById('imagePreview');
      imagePreview.style.backgroundImage = `url(${e.target.result})`;
      imagePreview.style.backgroundSize = 'cover';
      imagePreview.style.backgroundPosition = 'center';
      imagePreview.innerHTML = ''; // Xóa chữ "No Image"
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  // // Hàm xử lý khi submit form
  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   const requiredFields = ['fullname', 'email', 'phone', 'dob', 'cmnd'];
  //   for (let field of requiredFields) {
  //     if (!formData[field].trim()) {
  //       alert(`Vui lòng nhập ${field}!`);
  //       return;
  //     }
  //   }
  //   alert('Cập nhật thông tin thành công!');
  // };

//   for (let field of requiredFields) {
//     const input = document.getElementById(field);
//     if (!input.value.trim()) {
//         const fieldLabel = input.previousElementSibling.textContent.replace(':', '').replace('*', '').trim();
//         alert('Vui lòng nhập ' + fieldLabel + '!'); // Thông báo cho người dùng
//         input.focus(); // Đưa con trỏ đến ô chưa được điền
//         valid = false; // Đánh dấu rằng biểu mẫu không hợp lệ
//         e.preventDefault(); // Ngăn chặn việc gửi biểu mẫu
//         break; // Thoát khỏi vòng lặp
//     }
// }
      // Hàm xử lý khi submit form
      const handleSubmit = (e) => {
        e.preventDefault();
        const requiredFields = ['fullname', 'email', 'phone', 'dob', 'cmnd', 'address'];
        const fieldLabels = {
          fullname: 'Họ tên',
          email: 'Email',
          phone: 'Số điện thoại',
          dob: 'Ngày sinh',
          cmnd: 'CMND',
          address: 'Địa chỉ'
          // Thêm các trường khác nếu cần
        };

        let valid = true;
        let newErrors = {};

        for (let field of requiredFields) {
          if (!formData[field].replace(':', '').replace('*', '').trim()) {
            newErrors[field] = `Vui lòng nhập ${fieldLabels[field]}!`;
            valid = false;
          }
        }

        if (!valid) {
          setErrors(newErrors);
          // Tự động cuộn đến phần đầu của form để người dùng thấy thông báo lỗi
          window.scrollTo({ top: 0, behavior: 'smooth' });
          return;
        }

        // Nếu tất cả các trường hợp lệ
        alert('Cập nhật thông tin thành công!');
        // Thực hiện các hành động tiếp theo như gửi dữ liệu lên server
      };



  return (
    <div>
      <h2>Thông tin tài khoản</h2>
      <form id="memberForm" onSubmit={handleSubmit} noValidate encType="multipart/form-data">
        <div className="image-upload-container">
          <div id="imagePreview" className="image-preview" onClick={() => document.getElementById('profilePicture').click()}>
            <span>No Image</span>
          </div>
          <input
            type="file"
            id="profilePicture"
            name="profilePicture"
            accept="image/*"
            style={{ display: 'none' }}
            onChange={previewImage}
          />
          <button type="button" onClick={() => document.getElementById('profilePicture').click()}>Tải ảnh lên</button>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="fullname"><span className="highlight">* </span>Họ tên:</label>
            <input
              type="text"
              id="fullname"
              name="fullname"
              value={formData.fullname}
              onChange={handleChange}
              className="input"
              placeholder="Họ tên"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email"><span className="highlight">* </span>Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="input"
              placeholder="Email"
              required
            />
          </div>
        </div>

        {/* Các trường khác tương tự */}
      <div className="form-row">
          <div className="form-group">
            <label htmlFor="phone"><span className="highlight">* </span>Số điện thoại:</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className="input"
              placeholder="Số điện thoại"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="cmnd"><span className="highlight">* </span>CMND/Hộ chiếu:</label>
            <input
              type="card"
              id="cmnd"
              name="cmnd"
              value={formData.cmnd}
              onChange={handleChange}
              className="input"
              placeholder="CMND/Hộ chiếu"
              required
            />
          </div>
        </div>

       {/* Hàng ngày sinh và Giới tính */}
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="dob"><span className="highlight">* </span>Ngày sinh:</label>
            <input
              type="date"
              id="dob"
              name="dob"
              value={formData.dob}
              onChange={handleChange}
              className="input"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="gender">Giới tính:</label>
            <select
              id="gender"
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              className="input"
            >
              <option value="" disabled>Chọn giới tính</option>
              <option value="Nam">Nam</option>
              <option value="Nữ">Nữ</option>
              <option value="Khác">Khác</option>
            </select>
          </div>
          </div>


          <div className="form-row">
          <div className="form-group">
            <label htmlFor="city">Tỉnh/Thành phố:</label>
            <select
              id="city"
              name="city"
              value={formData.city}
              onChange={handleChange}
              className="input"
              placeholder="Tỉnh/Thành phố"
            >
              <option value="" disabled>Chọn tỉnh/thành phố</option>
              <option value="Ho Chi Minh">Hồ Chí Minh</option>
              <option value="Ha Noi">Hà Nội</option>
              <option value="Da Nang">Đà Nẵng</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="district">Quận/Huyện:</label>
            <input
              list="districts"
              id="district"
              name="district"
              value={formData.district}
              onChange={handleChange}
              className="input"
              autoComplete="off"
            />
            <datalist id="districts">
              {districts.map((district, index) => (
                <option key={index} value={district} />
              ))}
            </datalist>
          </div>
        </div>



        <div className="form-group">
          <label htmlFor="address"><span className="highlight">* </span>Địa chỉ:</label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className="input"
            placeholder="Địa chỉ"
            required
          />
        </div>


        <button type="submit">Cập nhật</button>
      </form>
    </div>
  );
}
