import React, { useState } from 'react';
import styles from './d2.module.css';
import AHDtheaterImg1 from './theaterImage/AHDtheater-1.png';
import AHDtheaterImg2 from './theaterImage/AHDtheater-2.png';
import AHDtheaterImg3 from './theaterImage/AHDtheater-3.png';
import AHDtheaterImg4 from './theaterImage/AHDtheater-4.png';

images=[AHDtheaterImg1,AHDtheaterImg2,AHDtheaterImg3,AHDtheaterImg4]
const Slider = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className={styles['slider-container']}>
      <div
        className={styles['slider-track']}
        style={{ transform: `translateX(-${currentIndex * 100}%)` }}
      >
        {images.map((image, index) => (
          <div
            key={index}
            className={styles['slide']}
            style={{ backgroundImage: `url(${image})` }}
          ></div>
        ))}
      </div>
      <button className={`${styles['nav-button']} ${styles['prev']}`} onClick={prevSlide}>
        ❮
      </button>
      <button className={`${styles['nav-button']} ${styles['next']}`} onClick={nextSlide}>
        ❯
      </button>
    </div>
  );
};

export default Slider;
