import React, { useState, useEffect } from 'react';
import "../style.css";

export default function CinemaList() {

    // Sử dụng useState để quản lý trạng thái hiển thị của từng tỉnh thành
    const [selectedProvince, setSelectedProvince] = useState(null);
    // Sử dụng useState để quản lý trạng thái hiển thị của rạp phim
    const [selectedTheater, setSelectedTheater] = useState(null);
    // Hàm để xử lý sự kiện khi nhấn vào tỉnh thành

    const handleProvinceClick = (province) => {
        // Nếu tỉnh thành đã chọn là Hồ Chí Minh,..., sẽ ẩn khi nhấn lại, nếu không thì hiển thị
        setSelectedProvince(selectedProvince === province ? null : province);
        setSelectedTheater(null);
    };

    const handleTheaterClick = (theater) => {
        setSelectedTheater(selectedTheater === theater ? null : theater);
    };

    return (
        <div className="theater-wrap">
            <div className="theater-list-preview">
                <div className= "theater-showtime-top"></div>
                <div className = "theater-showtime-center">
                    <div className = "title-ahdCinema">
                        <h2>AHD Cinema</h2>
                    </div>
                    <div className = "cinemas-area-1">
                        <ul className="cinemas-byProvince">
                            <li onClick={() => handleProvinceClick("TPHoChiMinh")}>
                                Hồ Chí Minh
                                {selectedProvince === "TPHoChiMinh" && (
                                    <span className="underline"></span>
                                )}
                            </li>
                            <li onClick={() => handleProvinceClick("HaNoi")}>
                                Hà Nội
                                {selectedProvince === "HaNoi" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("DaNang")}>
                                Đà Nẵng
                                {selectedProvince === "DaNang" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("HaiPhong")}>
                                Hải Phòng
                                {selectedProvince === "HaiPhong" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                            <li onClick={() => handleProvinceClick("CanTho")}>
                                Cần Thơ
                                {selectedProvince === "CanTho" && (
                                    <span className="underline"></span>
                                )}
                            </li> 
                        </ul>
                    <div className ="cinemas-area-2">
                        <ul className ="cinemas-list">
                        {selectedProvince === "TPHoChiMinh" && (
                            <>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Hùng Vương Plaza")}>AHD Hùng Vương Plaza</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Crescent Mall")}>AHD Crescent Mall</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Thảo Điền Pearl")}>AHD Thảo Điền Pearl</li>
                            <li  className="ahd-city-1"onClick={() => handleTheaterClick("AHD Vincom Thủ Đức")}>AHD Vincom Thủ Đức</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vivo City")}>AHD Vivo City</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Pearl Plaza")}>AHD Pearl Plaza</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Liberty Citypoint")}>AHD Liberty Citypoint</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Đồng Khởi")}>AHD Vincom Đồng Khởi</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Menas Mall")}>AHD Menas Mall</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Pandora City")}>AHD Pandora City</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Aeon Tân Phú")}>AHD Aeon Tân Phú</li>
                            <li  className="ahd-city-1" onClick={() => handleTheaterClick("AHD Vincom Gò Vấp")}>AHD Vincom Gò Vấp</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Hoàng Văn Thụ")}>AHD Hoàng Văn Thụ</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Aeon Bình Tân")}>AHD Aeon Bình Tân</li>
                            <li className="ahd-city-1" onClick={() => handleTheaterClick("AHD Saigonres Nguyễn Xí")}>AHD Saigonres Nguyễn Xí</li>
                            </>
                        )}
                        {selectedProvince === "HaNoi" && (
                            <>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Bà Triệu")}>AHD Vincom Bà Triệu</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Hồ Gươm Plaza")}>AHD Hồ Gươm Plaza</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Aeon Long Biên")}>AHD Aeon Long Biên</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Nguyễn Chí Thanh")}>AHD Vincom Nguyễn Chí Thanh</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Indochine Plaza Hà Nội")}>AHD Indochine Plaza Hà Nội</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Rice City")}>AHD Rice City</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Hà Nội Centerpoint")}>AHD Hà Nội Centerpoint</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Royal City")}>AHD Vincom Royal City</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Times City")}>AHD Vincom Times City</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Vincom Long Biên")}>AHD Vincom Long Biên</li>
                            <li className="ahd-city-2"onClick={() => handleTheaterClick("AHD Mac Plaza")}>AHD Mac Plaza</li>
