import React, { useRef } from "react";
import Slider from "react-slick";
import styles from "./cinemaList.module.css";
import AHDtheaterImg1 from './theaterImage/AHDtheater-1.png';
import AHDtheaterImg2 from './theaterImage/AHDtheater-2.png';
import AHDtheaterImg3 from './theaterImage/AHDtheater-3.png';
import AHDtheaterImg4 from './theaterImage/AHDtheater-4.png';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';

// Nút tùy chỉnh với Font Awesome cho next arrow
function NextArrow(props) {
  const { className, style, onClick } = props;
  return (
  
      <FontAwesomeIcon  
        icon={faArrowRight} 
        className={`${className} ${styles.customArrow} ${styles.nextArrow}`} 
        style={{ ...style,  marginRight: "2px"}} 
        onClick={onClick}
      />

  );
}
// Nút tùy chỉnh với Font Awesome cho prev arrow
function PrevArrow(props) {
  const { className, style, onClick } = props;
  return (

      <FontAwesomeIcon  
        icon={faArrowLeft} 
        className={`${className} ${styles.customArrow} ${styles.prevArrow}`} 
        style={{ ...style, marginLeft: "20px" }} 
        onClick={onClick}
        
      />

  );
}


export default function TheaterDetail() {
  const sliderRef = useRef(null); // Sử dụng useRef để tham chiếu đến slider

  var settings = {
    dots: true,
    infinite: true,
    speed: 2000,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    autoplay: true,           // Bật tính năng tự động chuyển
    autoplaySpeed: 4500,      // Tự chuyển sau mỗi 3000 milliseconds (3 giây)
  };

  return (
    <div className={styles.sliderContainer}>
      {/* <Slider {...settings} ref={sliderRef}>
        <div >
          <img 
              src={AHDtheaterImg1}
              alt="AHD Theater"
              className={styles.sliderImg}
          />
        </div>
        <div >
          <img 
              src={AHDtheaterImg2}
              alt="AHD Theater"
              className={styles.sliderImg}
          />
        </div>
        <div >
          <img 
              src={AHDtheaterImg3}
              alt="AHD Theater"
              className={styles.sliderImg}
          />
        </div>
        <div >
          <img 
              src={AHDtheaterImg4}
              alt="AHD Theater"
              className={styles.sliderImg}
          />
        </div>
      </Slider> */}
      <Slider {...settings} ref={sliderRef}>
  {[AHDtheaterImg1, AHDtheaterImg2, AHDtheaterImg3, AHDtheaterImg4].map((img, index) => (
    <div key={index}>
      <img src={img} alt={`AHD Theater ${index + 1}`} className={styles.sliderImg} />
    </div>
  ))}
</Slider>

      {/* Khung nội dung chung bên ngoài slider */}
      <div className = {styles.contentOverlay}>
        <div style={{marginLeft:'2vw', padding:'2vw'}}>
            <p>Tầng 3| Hùng Vương Plaza, Quận 5, TP.Hồ Chí Minh</p>
            <p>
              <span style={{fontWeight: '800'}}>
                Fax : 
              </span>
               {' '}+84 0123456789
            </p>
            <p>
              <span style={{fontWeight: '800'}}>
                Hotline : 
              </span>
               {' '}1900 6868
            </p>
        </div>

        <div className={styles.buttonContentOverlay} style={{marginLeft:'2vw', padding:'2vw', margin:'2vw'}}>
          <button style={{backgroundColor:'red'}}>
            Xem bản đồ
          </button>
          <button style={{backgroundColor:'red'}}>
            Liên Hệ AHD
          </button >
        </div>
            

      </div>
    </div>
  );
}





