/**
 * Hàm kiểm tra các trường bắt buộc trong form.
 * @param {Object} formData - Dữ liệu của form.
 * @param {Array} requiredFields - Danh sách các trường bắt buộc.
 * @param {Object} fieldLabels - Đối tượng ánh xạ giữa tên trường và nhãn hiển thị.
 * @returns {Object} - Kết quả kiểm tra với các thông báo lỗi và trạng thái hợp lệ.
 */
export const validateForm = (formData, requiredFields, fieldLabels) => {
    let valid = true;
    let newErrors = {};
    let firstError = '';
  
    for (let field of requiredFields) {
      if (!formData[field].trim()) {
        newErrors[field] = `Vui lòng nhập ${fieldLabels[field]}!`;
        if (!firstError) {
          firstError = newErrors[field];
        }
        valid = false;
      }
    }
  
    return { valid, errors: newErrors, firstError };
  };
